package webDriverPackage;

public class ClassForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// for loop class
		// if i>0 it will show infinite loop and machine will crash, it keeps running
		
		// print 1 to 10 even number
		
		int i;
		for (i=2; i<=10; i = i+2)
		{
			System.out.println(i);
		}
	
		
		// print 1 to 10 odd number
		
		int i2;
		for (i2=1; i2<=10; i2=i2+2)
		{
			System.out.println(i2);
		}

		
		
		
	}

}
