package webDriverPackage;

public class NoteWhileCondition03122017 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// While Condition it's for infinite loop
		

		int i=90;
		while (i>=90)
		{
			System.out.println("You receive an A");
		}
		
		//make it stop running infinite
		int i2 =90;
		while (i2>=90)
		{
			i2= i2+1;
		}
			System.out.println("You receive an A");
				


	}

}
