package webDriverPackage;

public class Writeprogram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Write a program that will show: The price is = $ 150.25
		
		// First declare the variable 
		double num2;
		num2= 150.25;
		
		// Then print out 
		System.out.println("The price is = $ " +num2);
		
		// Shortcut
		double num4 = 190.00;
		System.out.println("The price is = $ " +num4);	
		
		// print out a letter
		char letter;
		letter= 'b';
		System.out.println(letter);
		
		//print out the word
		String word;
		word = "hello";
		System.out.println("The word is " +word);
		
		
		//boolean is true or false
		
		
	}

}
